"use strict";function getTimestamp(){return"["+chalk.grey(timestamp("HH:mm:ss"))+"]"}function log(){var e=getTimestamp();return process.stdout.write(e+" "),console.log.apply(console,arguments),this}function info(){var e=getTimestamp();return process.stdout.write(e+" "),console.info.apply(console,arguments),this}function dir(){var e=getTimestamp();return process.stdout.write(e+" "),console.dir.apply(console,arguments),this}function warn(){var e=getTimestamp();return process.stderr.write(e+" "),console.warn.apply(console,arguments),this}function error(){var e=getTimestamp();return process.stderr.write(e+" "),console.error.apply(console,arguments),this}var chalk=require("chalk"),timestamp=require("time-stamp");module.exports=log,module.exports.info=info,module.exports.dir=dir,module.exports.warn=warn,module.exports.error=error;